@import url('https://fonts.googleapis.com/css2?family=Roboto+Condensed:wght@300;400;700&display=swap');

/*CSS Variables*/
$main-color: #2e4c7e;
$main-color-rgba: rgba(46, 76, 126, 0.5);
$tint-color: #e2a031;
$secondary-color: #647db3;
$off-white: #e1f0fe;
$white: #ffffff;
$main-font: 'Roboto Condensed', sans-serif;
$bg-color: #eee;

* {
  border: 0;
  box-sizing: border-box;
  margin: 0;
}

body {
  font-family: $main-font;
  margin: 0;
  height: auto;
  background: $bg-color;
}

main {
  max-width: 1000px;
  margin: auto;
  box-shadow: 30px 0px 40px rgba(0, 0, 0, 0.1),
    -30px 0px 40px rgba(0, 0, 0, 0.1);
}

h1 {
  font-weight: 400;
  font-size: 3rem;
  margin: 0;
  color: $off-white;
  text-transform: uppercase;
}

h2 {
  font-weight: 400;
  font-size: 1.5rem;
  margin: 0;
  color: $secondary-color;
  text-transform: uppercase;
}

img {
  display: block;
  width: 100%;
}

#landing {
  background: $main-color;
}

#landing-text {
  display: flex;
  flex: 0 1 40vw;
  height: 65vh;
  justify-content: center;
  align-items: center;
  text-align: center;
  padding: 1rem;
}

#landing-text-inner h1 {
  margin-top: 0px;
}

#landing-logo {
  display: block;
  width: 90%;
  margin: auto;
  -webkit-animation: swing-in-top-fwd 1.5s
    cubic-bezier(0.175, 0.885, 0.32, 1.275) both;
  animation: swing-in-top-fwd 1.5s cubic-bezier(0.175, 0.885, 0.32, 1.275) both;
}

.btn {
  padding: 0.25rem 0.5rem;
  border-right: 2px solid $tint-color;
  border-left: 2px solid $tint-color;
  display: inline-block;
  margin: 2rem 1rem;
  color: $secondary-color;
  text-decoration: none;
  font-size: 1.25rem;
  background: linear-gradient(to right, $tint-color 50%, $main-color 50%);
  background-size: 200% 100%;
  background-position: right bottom;
  -webkit-animation: swing-in-top-fwd 2s cubic-bezier(0.175, 0.885, 0.32, 1.275)
    both;
  animation: swing-in-top-fwd 2s cubic-bezier(0.175, 0.885, 0.32, 1.275) both;
  transition: all 500ms ease;
}
.btn:hover {
  background-position: left bottom;
  opacity: 0.9;
  color: $main-color;
}

.btn-light {
  color: $main-color;
  background: linear-gradient(to right, $main-color 50%, $off-white 50%);
  background-size: 200% 100%;
  background-position: right bottom;
}

.btn-light:hover {
  color: $secondary-color;
}

#landing-image {
  background: linear-gradient($main-color-rgba, $main-color-rgba),
    url('/img/bg-image.jpg');
  background-position: center;
  background-size: cover;
  background-repeat: none;
  height: 35vh;
  flex: 0 1 60vh;
  margin: 0;
  transition: all 500ms;
}

#landing-image:hover {
  filter: blur(3px);
}

#about {
  padding: 1rem;
  text-align: center;
  background: $off-white;
}

#profile-pic-area {
  display: none;
}
#profile-pic {
  border-radius: 2%;
  border: 2px solid $main-color;
  width: 35vw;
}

#about-content p {
  font-size: 1rem;
  font-weight: 300;
  color: $main-color;
  line-height: 1.5rem;
  margin-bottom: 1rem;
}

#about-social {
  margin-top: 30px;
  color: $main-color;
}

#about-social i {
  color: $main-color;
  font-size: 2rem;
  display: inline-block;
  margin: 0 20px;
  transition: all 500ms;
}

#about-social i:hover {
  cursor: pointer;
  color: $tint-color;
}

#work {
  padding: 1rem;
  text-align: center;
  background: $main-color;
  color: $off-white;
}

#work-content h2 {
  border-right: 2px solid $tint-color;
  border-left: 2px solid $tint-color;
  margin: 0.5rem 4rem;
  text-transform: none;
  color: $secondary-color;
  text-decoration: none;
  font-size: 1.5rem;
}

//SKILL AREA STYLING

#profile-skills-title {
  color: $main-color;
  font-size: 1.25rem;
  margin: 0.75rem;
}

.btn-skill {
  background-color: red;
  color: white;
  font-size: 13px;
  padding: 3px 7px;
  margin-bottom: 10px;
  box-shadow: none;
  outline: 0;
  display: inline-block;
  border-radius: 0.25em;
  font-weight: 500;
}

.html {
  background-color: #e34928;
}

.css,
.sql {
  background-color: #2873b8;
}

.javascript {
  background-color: #eedc5a;
  color: $secondary-color;
}

.sass {
  background-color: #ce659a;
}

.react,
.wordpress {
  background-color: #4597ac;
}

.swift {
  background-color: #e74227;
}

.node {
  background-color: #8ec64a;
  color: $secondary-color;
}
.angular {
  background-color: #c2002d;
}

.express {
  background-color: $secondary-color;
}

.bootstrap {
  background-color: #563a78;
}

.vue,
.mongo {
  background-color: #35976d;
}

// MEDIA RULES
@media (min-width: 500px) {
  body {
    margin: auto;
    max-width: 900px;
  }

  #landing {
    display: flex;
    height: 100%;
    background: $bg-color;
    margin: auto;
  }
  #landing-text {
    height: 100vh;
    background: $main-color;
    flex: 0 1 50vw;
  }
  #landing-image {
    height: 100vh;
    flex: 0 1 50vw;
  }

  #work-content h2 {
    margin: auto;
    padding: 0 20px;
    display: inline-block;
  }
}

// CSS ANIMATIONS

@-webkit-keyframes swing-in-top-fwd {
  0% {
    -webkit-transform: rotateX(-100deg);
    transform: rotateX(-100deg);
    -webkit-transform-origin: top;
    transform-origin: top;
    opacity: 0;
  }
  100% {
    -webkit-transform: rotateX(0deg);
    transform: rotateX(0deg);
    -webkit-transform-origin: top;
    transform-origin: top;
    opacity: 1;
  }
}
@keyframes swing-in-top-fwd {
  0% {
    -webkit-transform: rotateX(-100deg);
    transform: rotateX(-100deg);
    -webkit-transform-origin: top;
    transform-origin: top;
    opacity: 0;
  }
  100% {
    -webkit-transform: rotateX(0deg);
    transform: rotateX(0deg);
    -webkit-transform-origin: top;
    transform-origin: top;
    opacity: 1;
  }
}

